import requests

BASE_URL = "http://localhost:3001"
TIMEOUT = 30
HEADERS = {
    "Content-Type": "application/json"
}

def test_post_api_calls_start_outbound_call_initiation():
    url = f"{BASE_URL}/api/calls/start"
    payload = {
        "leads": [
            {"phone": "+1234567890"},
            {"phone": "+1987654321"}
        ],
        "vapiAgentId": "agent123",
